import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service'

@Component({
  selector: 'app-admin-publication',
  templateUrl: './admin-publication.component.html',
  styleUrls: ['./admin-publication.component.css']
})
export class AdminPublicationComponent implements OnInit {
  publications : any[] = [
    {
      "title": "A low-complexity geometric bilateration method for localization in wireless sensor networks and its comparison with least-squares methods",
      "authors": "Juan Cota-Ruiz, Jose-Gerardo Rosiles, Ernesto Sifuentes, Pablo Rivas-Perea",
      "doi": "10.3390/s120100839",
      "journal": "Sensors",
      "date": "2012/1",
      "users": ["pablo_rivas", "ernesto_sifuentes"]                
    },
    {
      "title": "A Review of Machine Learning and Cryptography Applications",
      "authors": "Korn Sooksatra, Pablo Rivas",
      "doi": "10.1109/CSCI51800.2020.00105",
      "journal": "2020 International Conference on Computational Science and Computational Intelligence (CSCI)",
      "date": "2020/12",
      "users": ["korn_sooksatra","pablo_rivas"]                
    },
    {
      "title": "An Adversarial Neural Cryptography Approach to Integrity Checking: Learning to Secure Data Communications",
      "authors": "Korn Sooksatra, Pablo Rivas",
      "doi": "10.1109/IJCNN52387.2021.9534000",
      "journal": "2021 International Joint Conference on Neural Networks (IJCNN)",
      "date": "2021/7",
      "users": ["korn_sooksatra","pablo_rivas"]                
    }
  ];

  active_publications : any[] = [];

  search_title : string = '';
  search_authors : string = '';
  search_doi : string = '';
  search_journal : string = '';
  search_date : string = '';
  search_users : string = '';

  add_title : string = '';
  add_authors : string = '';
  add_doi : string = '';
  add_journal : string = '';
  add_date : string = '';
  add_users : string = '';
  constructor(private api: ApiService) { }

  ngOnInit(): void {
    this.loadScript('../assets/js/ruang-admin.min.js');
    this.active_publications = [...this.publications]
    this.getPublications();
  }

  getPublications() {
    this.api.getPublications().subscribe(data => {
      for (const d of (data as any)) {
        this.publications.push({
          title: d.title,
          authors: d.authors,
          doi: d.doi,
          journal: d.journal,
          date: d.date,
          users: d.users
        });
      }
      console.log(this.publications);
    });
  }

  changeSearch(type: string, val: string) {
    if(type == "title") {
      this.search_title = val;
    }
    else if (type == "authors") {
      this.search_authors = val;
    }
    else if (type == "doi") {
      this.search_doi = val;
    }
    else if (type == "journal") {
      this.search_journal = val;
    }
    else if (type == "date") {
      this.search_date = val;
    }
    else if (type == "users") {
      this.search_users = val;
    }
    this.search();
  }
  search() {
    this.active_publications = [];
    for(var publication of this.publications) {
      let include: boolean = true;
      if(this.search_title != "") {
        include = include && publication.title.toLowerCase( ).includes(this.search_title.toLowerCase( ));
      }
      if(this.search_authors != "") {
        include = include && publication.authors.toLowerCase( ).includes(this.search_authors.toLowerCase( ));
      }
      if(this.search_doi != "") {
        include = include && publication.doi.toLowerCase( ).includes(this.search_doi.toLowerCase( ));
      }
      if(this.search_journal != "") {
        include = include && publication.journal.toLowerCase( ).includes(this.search_journal.toLowerCase( ));
      }
      if(this.search_date != "") {
        include = include && publication.date.toLowerCase( ).includes(this.search_date.toLowerCase( ));
      }
      if(this.search_users != "") {
        let user_level = false;
        for(var user of publication.users){
          user_level = user_level || user.toLowerCase( ).includes(this.search_users.toLowerCase());
        }
        include = include && user_level;
      }
      if(include) {
        this.active_publications.push(publication);
      }
    }    

  }

  block(index: number, user: number){
    this.publications[index].users.splice(user, 1);
  }

  remove(index: number){
    this.publications.splice(index, 1);
  }

  add() {
    let users = this.add_users.replace(/\s/g, "").split(";").filter(function (el) {
      return el != "";
    });
    let temp = {
        "title": this.add_title,
        "authors": this.add_authors,
        "doi": this.add_doi,
        "journal": this.add_journal,
        "date": this.add_date,
        "users": users
    };
    this.publications.push(temp);  
    this.active_publications = [...this.publications]
    this.add_title = '';
    this.add_authors = '';
    this.add_doi = '';
    this.add_journal = '';
    this.add_date = '';
    this.add_users = '';  
  }

  changeTitle(title: string) {
    this.add_title = title;
  }

  changeAuthors(authors: string) {
    this.add_authors = authors;
  }

  changeDOI(doi: string) {
    this.add_doi = doi;
  }

  changeJournal(journal: string) {
    this.add_journal = journal;
  }

  changeDate(date: string) {
    this.add_date = date;
  }

  changeUsers(users: string) {
    this.add_users = users;
  }
  public loadScript(url: string) {
    const body = <HTMLDivElement> document.body;
    const script = document.createElement('script');
    script.innerHTML = '';
    script.src = url;
    script.async = false;
    script.defer = true;
    body.appendChild(script);
  }

}
